import 'package:flutter/material.dart';
import 'package:flutter_hooks/flutter_hooks.dart';
import 'package:http/http.dart' as http;
import 'dart:convert';
import 'package:connectivity/connectivity.dart';

enum TableStatus { idle, loading, ready, error }

class DataService {
  final ValueNotifier<Map<String, dynamic>> tableStateNotifier = ValueNotifier(
      {'status': TableStatus.idle, 'dataObjects': [], 'columnNames': []});

  void carregar(index) async {
    final funcoes = [
      carregarCafes,
      carregarCervejas,
      carregarNacoes,
      carregarApps
    ];
    tableStateNotifier.value = {
      'status': TableStatus.loading,
      'dataObjects': [],
      'columnNames': [],
    };

    var connectivityResult = await Connectivity().checkConnectivity();
    if (connectivityResult != ConnectivityResult.none) {
      funcoes[index]();
    } else {
      print("Sem conex√£o com a internet!");
    }
  }

  void carregarApps() {
    var appsUri = Uri(
        scheme: 'https',
        host: 'random-data-api.com',
        path: 'api/app/random_app',
        queryParameters: {'size': '5'});
    http.read(appsUri).then((jsonString) {
      var appsJson = jsonDecode(jsonString);
      var properyNames = ["app_name", "app_version", "app_author"];
      tableStateNotifier.value = {
        'status': TableStatus.ready,
        'dataObjects': appsJson,
        'columnNames': properyNames,
      };
    });
  }

  void carregarCafes() async {
    var coffesUri = Uri(
        scheme: 'https',
        host: 'random-data-api.com',
        path: 'api/coffee/random_coffee',
        queryParameters: {'size': '5'});
    await http.read(coffesUri).then((jsonString) {
      var coffeesJson = jsonDecode(jsonString);
      var propertyNames = ["blend_name", "origin", "variety"];
      tableStateNotifier.value = {
        'status': TableStatus.ready,
        'dataObjects': coffeesJson,
        'columnNames': propertyNames,
      };
    });
  }

  void carregarNacoes() {
    var nationsUri = Uri(
        scheme: 'https',
        host: 'random-data-api.com',
        path: 'api/nation/random_nation',
        queryParameters: {'size': '5'});
    http.read(nationsUri).then((jsonString) {
      var nationsJson = jsonDecode(jsonString);
      var propertyNames = ["nationality", "language", "capital"];
      tableStateNotifier.value = {
        'status': TableStatus.ready,
        'dataObjects': nationsJson,
        'columnNames': propertyNames,
      };
    });
  }

  void carregarCervejas() {
    var beersUri = Uri(
        scheme: 'https',
        host: 'random-data-api.com',
        path: 'api/beer/random_beer',
        queryParameters: {'size': '5'});
    http.read(beersUri).then((jsonString) {
      var beersJson = jsonDecode(jsonString);
      var propertyNames = ["name", "style", "ibu"];
      tableStateNotifier.value = {
        'status': TableStatus.ready,
        'dataObjects': beersJson,
        'columnNames': propertyNames,
      };
    });
  }
}

final dataService = DataService();

void main() {
  runApp(
    MaterialApp(
      home: MediaQuery(
        data: MediaQueryData(),
        child: MyApp(),
      ),
    ),
  );
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      theme: ThemeData(primarySwatch: Colors.green),
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        appBar: AppBar(
          toolbarHeight: 50,
          flexibleSpace: FlexibleSpaceBar(
            background: Image.network(
              'data:image/jpeg;base64,',
              fit: BoxFit.cover,
            ),
          ),
          title: Text(
            "Programa√ß√£o Ass√≠ncrona",
            style: TextStyle(
              color: Colors.black,
            ),
          ),
          textTheme: TextTheme(
            headline6: TextStyle(
              color: Colors.black,
              fontSize: 20,
              fontWeight: FontWeight.bold,
            ),
          ),
        ),
        body: ValueListenableBuilder(
          valueListenable: dataService.tableStateNotifier,
          builder: (_, value, __) {
            switch (value['status']) {
              case TableStatus.idle:
                return Center(
                  child: Column(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: [
                      Image.network(
                        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQF898IUjVuyp-DEeSjy7C-9CXvGyiRn2BkOw&usqp=CAU',
                        width: 200,
                        height: 200,
                      ),
                      SizedBox(height: 20),
                      Text(
                        'Melhores cervejas, caf√©s e na√ß√µes prontinhas para voc√™!',
                        style: TextStyle(fontSize: 30),
                      ),
                      SizedBox(height: 30),
                      Text(
                        'Toque em um dos bot√µes abaixo para desfrutar dos dados.',
                        style: TextStyle(fontSize: 16),
                        textAlign: TextAlign.center,
                      ),
                    ],
                  ),
                );
              case TableStatus.loading:
                return Center(
                  child: CircularProgressIndicator(),
                );
              case TableStatus.ready:
                return DataTableWidget(
                  jsonObjects: value['dataObjects'],
                  columnNames: value['columnNames'],
                );
              case TableStatus.error:
                return Text("Lascou");
            }
            return Text("...");
          },
        ),
        bottomNavigationBar:
            NewNavBar(itemSelectedCallback: dataService.carregar),
      ),
    );
  }
}

class NewNavBar extends HookWidget {
  final _itemSelectedCallback;

  NewNavBar({itemSelectedCallback})
      : _itemSelectedCallback = itemSelectedCallback ?? (int) {}

  @override
  Widget build(BuildContext context) {
    var state = useState(1);
    return BottomNavigationBar(
      onTap: (index) {
        state.value = index;
        _itemSelectedCallback(index);
      },
      currentIndex: state.value,
      selectedItemColor: Colors.green,
      unselectedItemColor: Colors.green,
      items: const [
        BottomNavigationBarItem(
          label: "Caf√©s",
          icon: Icon(Icons.coffee_outlined),
        ),
        BottomNavigationBarItem(
          label: "Cervejas",
          icon: Icon(Icons.local_drink_outlined),
        ),
        BottomNavigationBarItem(
          label: "Na√ß√µes",
          icon: Icon(Icons.flag_outlined),
        ),
        BottomNavigationBarItem(
          label: "Apps",
          icon: Icon(Icons.apps_outlined),
        ),
      ],
    );
  }
}

class DataTableWidget extends StatelessWidget {
  final List jsonObjects;
  final List<String> columnNames;

  DataTableWidget({
    this.jsonObjects = const [],
    this.columnNames = const ["Nome", "Estilo", "IBU"],
  });

  @override
  Widget build(BuildContext context) {
    return DataTable(
      columns: columnNames
          .map(
            (name) => DataColumn(
              label: Expanded(
                child: Text(
                  name,
                  style: TextStyle(fontStyle: FontStyle.italic),
                ),
              ),
            ),
          )
          .toList(),
      rows: jsonObjects
          .map(
            (obj) => DataRow(
              cells: columnNames
                  .map(
                    (propName) => DataCell(
                      Text(obj[propName]),
                    ),
                  )
                  .toList(),
            ),
          )
          .toList(),
    );
  }
}
